	    , password
	    , profile_img_url
	    , bio
	    , birth_date
	    , gender
	    , register_date
	    , registered_user_id
	    , update_date
	    , updated_user_id
	) 
	VALUES ( 
	    #{userId}
	    , #{userName}
	    , #{emailAdress}
	    , #{phoneNumber}
	    , #{password}
	    , #{profileImgUrl}
	    , #{bio}@bio
	    , #{birthDate}
	    , #{gender}
	    , #{registerDate}
	    , #{registeredUserId}
	    , #{updateDate}
	    , #{updatedUserId}
	);
