mapper.select:
SELECT recipe_id , recipe_name , recipe_ave_rating , rating_total_count , recipe_img , recipe_descrip , publish_status , register_date , update_date FROM recipe_info WHERE user_id = #{userId}
AND delete_flag = #{deleteFlag}
ORDER BY register_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT recipe_id , recipe_name , recipe_ave_rating , rating_total_count , fav_total_count , recipe_img , recipe_descrip , publish_status , register_date , update_date , ( ( recipe_ave_rating / 5.0 + 1.96 * 1.96 / (2 * rating_total_count) - 1.96 * sqrt( (recipe_ave_rating / 5.0) * (1 - recipe_ave_rating / 5.0) / rating_total_count + 1.96 * 1.96 / (4 * rating_total_count * rating_total_count)) ) / (1 + 1.96 * 1.96 / rating_total_count) ) AS wilson_score FROM recipe_info WHERE rating_total_count > 0 AND update_date >= NOW() - INTERVAL '7 days' AND registered_user_id != #{registeredUserId} AND delete_flag = #{deleteFlag} AND publish_status = '0'
AND( ri.user_id LIKE CONCAT('%', #{searchCondition}, '%') OR ri.recipe_id LIKE CONCAT('%', #{searchCondition}, '%') OR ri.recipe_name LIKE CONCAT('%', #{searchCondition}, '%') OR ri.recipe_descrip LIKE CONCAT('%', #{searchCondition}, '%') OR ing.ingredient_name LIKE CONCAT('%', #{searchCondition}, '%') OR step.instruction LIKE CONCAT('%', #{searchCondition}, '%'))
ORDER BY wilson_score DESC;

SELECT recipe_id , recipe_name , recipe_ave_rating , rating_total_count , fav_total_count , recipe_img , recipe_descrip , publish_status , register_date , update_date FROM recipe_info WHERE registered_user_id != #{registeredUserId} AND delete_flag = #{deleteFlag} AND publish_status = '0'
AND( ri.user_id LIKE CONCAT('%', #{searchCondition}, '%') OR ri.recipe_id LIKE CONCAT('%', #{searchCondition}, '%') OR ri.recipe_name LIKE CONCAT('%', #{searchCondition}, '%') OR ri.recipe_descrip LIKE CONCAT('%', #{searchCondition}, '%') OR ing.ingredient_name LIKE CONCAT('%', #{searchCondition}, '%') OR step.instruction LIKE CONCAT('%', #{searchCondition}, '%'))
ORDER BY update_date DESC;

SELECT recipe_id , recipe_name , recipe_ave_rating , rating_total_count , fav_total_count , recipe_img , recipe_descrip , publish_status , register_date , update_date FROM recipe_info WHERE registered_user_id != #{registeredUserId} AND delete_flag = #{deleteFlag} AND publish_status = '0'
AND( ri.user_id LIKE CONCAT('%', #{searchCondition}, '%') OR ri.recipe_id LIKE CONCAT('%', #{searchCondition}, '%') OR ri.recipe_name LIKE CONCAT('%', #{searchCondition}, '%') OR ri.recipe_descrip LIKE CONCAT('%', #{searchCondition}, '%') OR ing.ingredient_name LIKE CONCAT('%', #{searchCondition}, '%') OR step.instruction LIKE CONCAT('%', #{searchCondition}, '%'))
ORDER BY fav_total_count DESC;

mapper.insert:
INSERT 
INTO public.recipe_info( 
    user_id
    , recipe_id
    , recipe_name
    , recipe_ave_rating
    , recipe_img
    , recipe_descrip
    , publish_status
    , delete_flag
    , register_date
    , registered_user_id
    , update_date
    , updated_user_id
) 
VALUES ( 
    #{userId}
    , #{recipeId}
    , #{recipeName}
    , #{recipeAveRating}
    , #{recipeImg}
    , #{recipeDescrip}
    , #{publishStatus}
    , #{deleteFlg}		    
    , #{registerDate}
    , #{registeredUserId}
    , #{updateDate}
    , #{updatedUserId}
);

mapper.update:
UPDATE public.recipe_info 


        user_id = #{userId}


        , recipe_name = #{recipeName}


