mapper.update:
 UPDATE public.user_detail 
SET
    user_name = #{userName}
    , email_address = #{emailAddress}
    , phone_number = #{phoneNumber}
    , profile_img_url = #{profileImgUrl}
    , bio = #{bio}
    , update_date = #{updateDate}
    , updated_user_id = #{updatedUserId}
WHERE
    user_id = #{userId};
